def hex2int:
  ascii_downcase | explode
  | reduce .[] as $c (0; . * 16 + (("0123456789abcdef"|explode) | index($c)));

((.result // "0x") | ltrimstr("0x") | .[0:64]) as $w
| [ { Finalized: ($w | hex2int) } ]

def hex2int:
  ascii_downcase | explode
  | reduce .[] as $c (0; . * 16 + (("0123456789abcdef"|explode) | index($c)));

def words:
  [range(0; length; 64) as $i | .[$i:$i+64]];

def as_array:
  if type == "array" then . else [.] end;

def byid($id):
  (as_array
   | map(select(type=="object" and ((.id|tostring) == ($id|tostring))))
   | .[0] // {});

def clamp_nonneg:
  if . > 0 then . else 0 end;

. as $resp
| ($resp | byid(1)) as $r1
| ($resp | byid(2)) as $r2
| ($resp | byid(3)) as $r3
| ($resp | byid(4)) as $r4
| ($resp | byid(5)) as $r5

| (($r1.result // "0x") | ltrimstr("0x") | .[0:64] | hex2int) as $epoch
| (($r4.result.timestamp // "0x0") | ltrimstr("0x") | hex2int) as $now
| (($r5.result // "0x") | ltrimstr("0x") | .[0:64] | hex2int) as $dur_s

| (($r2.result // "0x") | ltrimstr("0x") | words) as $odd
| (($r3.result // "0x") | ltrimstr("0x") | words) as $even
| (($odd[0] // "0") | hex2int) as $odd_start
| (($even[0] // "0") | hex2int) as $even_start
| (if ($epoch % 2) == 1 then $odd_start else $even_start end) as $start

| ($start + $dur_s) as $next
| (($next - $now) | clamp_nonneg) as $remain_s

| [ {
    "Epoch duration (min)": (($dur_s / 60) | floor),
    "Next epoch in (min)": (($remain_s / 60) | floor)
  } ]

